import './ExpenseDate.css'

function ExpenseDate(glubglub) {
    const month = glubglub.date.toLocaleString('en-US', { month: 'long' });
    const day = glubglub.date.toLocaleString('en-US', { day: '2-digit' });
    const year = glubglub.date.getFullYear();

    return (
        <div>
            <div>{month}</div>
            <div>{year}</div>
            <div>{day}</div>
        </div>
    )
}

export default ExpenseDate;
